mapper.select:
SELECT *
FROM pod_usage
WHERE pod_name = #{podName}
AND namespace = #{namespace}
ORDER BY time

SELECT *
FROM pod_usage
WHERE pod_name = #{podName}
AND namespace = #{namespace}
ORDER BY time DESC
LIMIT 1

SELECT *
FROM pod_usage
WHERE pod_name = #{podName}
  AND namespace = #{namespace}
AND time > #{time}
ORDER BY time DESC

SELECT *
FROM pod_usage
WHERE pod_name = #{podName}
AND namespace = #{namespace}
ORDER BY time DESC
